// src/App.js

import React, { useState } from "react";
import "./App.css";
import JobList from "./JobList.js";
import JobDetails from "./JobDetails.js";
import MaterialList from "./MaterialList.js";
import MaterialDetails from "./MaterialDetails.js";
import MatchList from "./MatchList.js";

function App() {
    const [showNextSteps, setShowNextSteps] = useState(false);

    const handleApplyNow = () => {
        setShowNextSteps(true);
    };

    // Define state variables for the selected job and the match percentage
    const [selectedJob, setSelectedJob] = useState(null);
    const [match, setMatch] = useState(0);

    // Define a function to handle job selection
    const handleJobSelect = (job) => {
        setSelectedJob(job); // Update state variable with selected job
        setMatch(Math.floor(Math.random() * 100) + 1); // Update state variable with random match percentage
    };

    // Define some dummy jobs as an array of objects
    const jobs = [
        {
            id: 1,
            title: "Web Developer",
            company: "ABC Inc.",
            location: "New York, NY",
            salary: 80000,
        },
        {
            id: 2,
            title: "Graphic Designer",
            company: "XYZ Ltd.",
            location: "Los Angeles, CA",
            salary: 60000,
        },
        {
            id: 3,
            title: "Data Analyst",
            company: "123 Corp.",
            location: "Chicago, IL",
            salary: 70000,
        },
    ];

    // Define state variables for the selected material and the show materials flag
    const [selectedMaterial, setSelectedMaterial] = useState(null);
    const [showMaterials, setShowMaterials] = useState(false);

    // Define a function to handle generate materials button click
    const handleGenerateMaterials = () => {
        setShowMaterials(true);
    };

    // Define a function to handle material selection
    const handleMaterialSelect = (material) => {
        setSelectedMaterial(material); // Update state variable with selected material
    };

    // Define some dummy materials as an array of objects
    const materials = [
        {
            id: 1,
            type: "Cover Letter",
            title: "Web Developer at ABC Inc.",
            summary:
                "A cover letter for the web developer position at ABC Inc. that showcases my interest and skills in web development.",
            content:
                "Dear Hiring Manager,\n\nI'm a web developer with three years of experience in building websites using HTML, CSS, JavaScript, React, and other web technologies. I have worked on projects for clients such as XYZ Ltd. and ABC Inc.\n\nI'm impressed by the work that ABC Inc. does in creating web solutions for various clients. I would love to join your team and contribute to your projects with my skills and enthusiasm.\n\nI have attached my resume and portfolio for your review. Please let me know if you have any questions or would like to schedule an interview.\n\nThank you for your consideration.\n\nSincerely,\nK",
        },
        {
            id: 2,
            type: "Resume",
            title: "Web Developer",
            summary:
                "A resume that highlights my skills and experience as a web developer using HTML, CSS, JavaScript, React, and other web technologies.",
            content:
                "K\nWeb Developer\nk@gmail.com | (123) 456-7890 | www.k.com\n\nSummary\nA web developer with three years of experience in building websites using HTML, CSS, JavaScript, React, and other web technologies.\n\nSkills\n- HTML, CSS, JavaScript\n- React, Redux, Next.js\n- Node.js, Express, MongoDB\n- Git, GitHub, VS Code\n- Responsive design, accessibility, SEO\n\nExperience\nWeb Developer | XYZ Ltd. | Jan 2020 - Present\n- Developed and maintained websites for various clients using React, Next.js, Node.js, Express, and MongoDB.\n- Implemented responsive design, accessibility, SEO best practices, and user authentication.\n- Collaborated with designers and project managers using Git and GitHub.\n- Tested and debugged code using VS Code and Chrome DevTools.\n\nWeb Developer | ABC Inc. | Jun 2018 - Dec 2019\n- Built and updated websites for various clients using HTML, CSS, JavaScript, jQuery, Bootstrap, and WordPress.\n- Added interactivity, animations, and transitions using JavaScript and jQuery.\n- Customized themes and plugins using WordPress.\n- Ensured cross-browser compatibility and mobile-friendliness using Bootstrap.\n\nEducation\nBachelor of Science in Computer Science | University of ABC | Sep 2015 - May 2018\n- Graduated with honors and a GPA of 3.8.\n- Completed courses in web development, database systems, software engineering, and data structures.\n- Worked on several projects using web technologies such as a portfolio website, a blog platform, and a chat app.\n\nPortfolio\nwww.k.com | A portfolio website that showcases my web development projects and skills.\nwww.xyz.com | A website for XYZ Ltd. that I developed using React, Next.js, Node.js, Express, and MongoDB.\nwww.abc.com | A website for ABC Inc. that I updated using HTML, CSS, JavaScript, jQuery, Bootstrap, and WordPress.",
        },
        {
            id: 3,
            type: "Portfolio",
            title: "Web Development Projects",
            summary:
                "A portfolio that showcases my web development projects and skills using HTML, CSS, JavaScript, React, and other web technologies.",
            content:
                "Web Development Projects\n\nPortfolio Website\nwww.k.com\nA portfolio website that showcases my web development projects and skills. I built this website using HTML, CSS, JavaScript, React, and Next.js. I used React to create reusable components and Next.js to enable server-side rendering and routing. I also used CSS to style the website and add animations and transitions.\n\nXYZ Website\nwww.xyz.com\nA website for XYZ Ltd. that I developed using React, Next.js, Node.js, Express, and MongoDB. I used React to create the user interface and Next.js to enable server-side rendering and routing. I used Node.js, Express, and MongoDB to create a backend server and a database that store and retrieve data. I also implemented user authentication, responsive design, accessibility, SEO best practices, and error handling.\n\nABC Website\nwww.abc.com\nA website for ABC Inc. that I updated using HTML, CSS, JavaScript, jQuery, Bootstrap, and WordPress. I used HTML, CSS, and JavaScript to add interactivity, animations, and transitions to the website. I used jQuery to simplify the DOM manipulation and event handling. I used Bootstrap to ensure cross-browser compatibility and mobile-friendliness. I used WordPress to customize themes and plugins.",
        },
    ];

    // Define state variables for the matches and the show matches flag
    const [matches, setMatches] = useState([]);
    const [showMatches, setShowMatches] = useState(false);

    // Define a function to handle find matches button click
    const handleFindMatches = () => {
        setShowMatches(true);
        setMatches(generateMatches()); // Generate some random matches
    };

    // Define a function to generate some random matches as an array of objects
    const generateMatches = () => {
        // Define some dummy companies as an array of objects
        const companies = [
            {
                id: 1,
                name: "ABC Inc.",
                industry: "Web Development",
                location: "New York, NY",
                logo: "https://via.placeholder.com/100x100?text=ABC",
            },
            {
                id: 2,
                name: "XYZ Ltd.",
                industry: "Graphic Design",
                location: "Los Angeles, CA",
                logo: "https://via.placeholder.com/100x100?text=XYZ",
            },
            {
                id: 3,
                name: "123 Corp.",
                industry: "Data Analysis",
                location: "Chicago, IL",
                logo: "https://via.placeholder.com/100x100?text=123",
            },
        ];

        // Define some dummy roles as an array of objects
        const roles = [
            {
                id: 1,
                title: "Web Developer",
                description:
                    "A web developer who can build websites using HTML, CSS, JavaScript, React, and other web technologies.",
                skills: ["HTML", "CSS", "JavaScript", "React"],
            },
            {
                id: 2,
                title: "Graphic Designer",
                description:
                    "A graphic designer who can create logos, flyers, posters, brochures, and other graphic materials.",
                skills: ["Photoshop", "Illustrator", "InDesign"],
            },
            {
                id: 3,
                title: "Data Analyst",
                description:
                    "A data analyst who can collect, analyze, and visualize data using SQL, Python, and Tableau.",
                skills: ["SQL", "Python", "Tableau"],
            },
        ];

// Define an empty array to store the matches

        let matches = [];

// Loop through the companies array

        // Loop through the companies array
        for (let company of companies) {
            // Loop through the roles array
            for (let role of roles) {
                // Generate a random match percentage between 1 and 100
                let match = Math.floor(Math.random() * 100) + 1;
                // Create a match object with the company, role, and match properties
                let matchObject = {
                    company: company,
                    role: role,
                    match: match,
                };
                // Push the match object to the matches array
                matches.push(matchObject);
            }
        }

// Return the matches array
        return matches;
    };

    return (
        <div className="App">
            <header>
                <h1>Name</h1>
            </header>
            <main>
                <section>
                    <h2>Apply to Recommended Jobs</h2>
                    <p>Apply to up to 10 recommended jobs per day with just one click.</p>
                    <button onClick={handleApplyNow}>Apply Now</button>
                </section>
                {showNextSteps && <JobList jobs={jobs} handleJobSelect={handleJobSelect} />}
                {selectedJob && <JobDetails job={selectedJob} match={match} />}
                <section>
                    <h2>Generate Personalized Application Materials</h2>
                    <p>Generate customized cover letters, resumes, and portfolios for each job you apply to.</p>
                    <button onClick={handleGenerateMaterials}>Generate Materials</button>
                </section>
                {showMaterials && <MaterialList materials={materials} handleMaterialSelect={handleMaterialSelect} />}
                {selectedMaterial && <MaterialDetails material={selectedMaterial} />}
                <section>
                    <h2>Find Your Perfect Matches</h2>
                    <p>Find out which companies and roles are the best fit for you based on your skills and preferences.</p>
                    <button onClick={handleFindMatches}>Find Matches</button>
                </section>
                {showMatches && <MatchList matches={matches} />}
                {/* Add the section for getting feedback here */}
                <section>
                    <h2>Get Expert Feedback and Advice</h2>
                    <p>Receive personalized feedback and advice from industry experts on your application materials and interview skills.</p>
                    <button onClick={handleGetFeedback}>Get Feedback</button>
                </section>
            </main>
        </div>
    );
}

export default App;
