import React, { useState, useEffect, useRef } from "react";

function Chatbot() {
    const [messages, setMessages] = useState([]);
    const [inputValue, setInputValue] = useState("");

    const botName = "JobBot"; // Name of the chatbot

    const chatbotRef = useRef(null);

    const addMessage = (text, isUser = false) => {
        setMessages((prevMessages) => [
            ...prevMessages,
            { text, isUser, timestamp: new Date().toISOString() },
        ]);
    };

    useEffect(() => {
        // Welcome message from the chatbot
        addMessage(`Hello! I'm ${botName}, your job application assistant. How can I help you?`);
    }, []);

    const handleUserMessage = () => {
        if (inputValue.trim() === "") return;
        addMessage(inputValue, true);
        // Implement logic to handle user input (e.g., answer questions, provide tips, etc.)
        setInputValue("");
    };

    useEffect(() => {
        // Scroll chat to the bottom when new messages are added
        chatbotRef.current.scrollTop = chatbotRef.current.scrollHeight;
    }, [messages]);

    return (
        <div className="chatbot">
            <div className="chatbot-header">{botName}</div>
            <div className="chatbot-messages" ref={chatbotRef}>
                {messages.map((message, index) => (
                    <div key={index} className={`message ${message.isUser ? "user" : "bot"}`}>
                        <span className="message-text">{message.text}</span>
                    </div>
                ))}
            </div>
            <div className="chatbot-input">
                <input
                    type="text"
                    value={inputValue}
                    onChange={(e) => setInputValue(e.target.value)}
                    placeholder="Type your message..."
                />
                <button onClick={handleUserMessage}>Send</button>
            </div>
        </div>
    );
}

export default Chatbot;
