// src/App.js

import React, { useState } from "react";
import "./App.css";
import JobList from "./JobList.js";
import JobDetails from "./JobDetails.js";
import MaterialList from "./MaterialList.js";
import MaterialDetails from "./MaterialDetails.js";
import MatchList from "./MatchList.js";
// Import FeedbackList and FeedbackDetails components
import FeedbackList from "./FeedbackList.js";
import FeedbackDetails from "./FeedbackDetails.js";

function App() {
    const [showNextSteps, setShowNextSteps] = useState(false);

    const handleApplyNow = () => {
        setShowNextSteps(true);
    };

    // Define state variables for the selected job and the match percentage
    const [selectedJob, setSelectedJob] = useState(null);
    const [match, setMatch] = useState(0);

    // Define a function to handle job selection
    const handleJobSelect = (job) => {
        setSelectedJob(job); // Update state variable with selected job
        setMatch(Math.floor(Math.random() * 100) + 1); // Update state variable with random match percentage
    };

    // Define some dummy jobs as an array of objects
    const jobs = [
        {
            id: 1,
            title: "Web Developer",
            company: "ABC Inc.",
            location: "New York, NY",
            salary: 80000,
        },
        {
            id: 2,
            title: "Graphic Designer",
            company: "XYZ Ltd.",
            location: "Los Angeles, CA",
            salary: 60000,
        },
        {
            id: 3,
            title: "Data Analyst",
            company: "123 Corp.",
            location: "Chicago, IL",
            salary: 70000,
        },
    ];

    // Define state variables for the selected material and the show materials flag
    const [selectedMaterial, setSelectedMaterial] = useState(null);
    const [showMaterials, setShowMaterials] = useState(false);

    // Define a function to handle generate materials button click
    const handleGenerateMaterials = () => {
        setShowMaterials(true);
    };

    // Define a function to handle material selection
    const handleMaterialSelect = (material) => {
        setSelectedMaterial(material); // Update state variable with selected material
    };

    // Define some dummy materials as an array of objects
    const materials = [
        {
            id: 1,
            type: "Cover Letter",
            title: "Web Developer at ABC Inc.",
            summary:
                "A cover letter for the web developer position at ABC Inc. that showcases my interest and skills in web development.",
            content:
                "Dear Hiring Manager,\n\nI'm a web developer with three years of experience in building websites using HTML, CSS, JavaScript, React, and other web technologies. I have worked on projects for clients such as XYZ Ltd. and ABC Inc.\n\nI'm impressed by the work that ABC Inc. does in creating web solutions for various clients. I would love to join your team and contribute to your projects with my skills and enthusiasm.\n\nI have attached my resume and portfolio for your review. Please let me know if you have any questions or would like to schedule an interview.\n\nThank you for your consideration.\n\nSincerely,\nK",
        },
        {
            id: 2,
            type: "Resume",
            title: "Web Developer",
            summary:
                "A resume that highlights my skills and experience as a web developer using HTML, CSS, JavaScript, React, and other web technologies.",
            content:
                "K\nWeb Developer\n\nSummary\nA web developer with three years of experience in building websites using HTML, CSS, JavaScript, React, and other web technologies.\n\nSkills\nHTML, CSS, JavaScript, React, Bootstrap, jQuery, Node.js, Express, MongoDB, Git, GitHub, VS Code, Photoshop\n\nExperience\nWeb Developer at XYZ Ltd. (Jan 2020 - Present)\n- Developed and maintained websites for various clients using HTML, CSS, JavaScript, React, and other web technologies.\n- Collaborated with designers and project managers to ensure the quality and functionality of the websites.\n- Implemented responsive design and user interface features using Bootstrap and jQuery.\n- Used Node.js, Express, and MongoDB to create backend services and databases for the websites.\n- Used Git and GitHub for version control and collaboration.\n\nWeb Developer at ABC Inc. (Jun 2018 - Dec 2019)\n- Built and deployed websites for various clients using HTML, CSS, JavaScript, React, and other web technologies.\n- Worked with designers and project managers to understand the requirements and specifications of the websites.\n- Added interactivity and dynamic features to the websites using JavaScript and React.\n- Used Photoshop to edit and optimize images for the websites.\n\nEducation\nBachelor of Science in Computer Science from University of X (2016 - 2020)\n- Graduated with a GPA of 3.8/4.0\n- Took courses in web development, database systems, software engineering, data structures, algorithms, etc.\n\nPortfolio\nhttps://k.github.io/portfolio/\n- A portfolio website that showcases some of my web development projects.",
        },
        {
            id: 3,
            type: "Portfolio",
            title: "Web Development Projects",
            summary:
                "A portfolio website that showcases some of my web development projects.",
            content:
                "https://k.github.io/portfolio/\n\nThis is a portfolio website that I created using HTML, CSS, JavaScript, React, and other web technologies. It showcases some of my web development projects that I have done for various clients or as personal projects. Some of the projects are:\n\n- A landing page for a travel agency that uses Bootstrap and jQuery to create a responsive and interactive design.\n- A blog website that uses Node.js, Express, and MongoDB to create a backend service and database for storing and retrieving blog posts.\n- A todo list app that uses React and localStorage to create a simple and user-friendly interface for managing tasks.\n- A weather app that uses the OpenWeatherMap API to fetch and display the current weather information for any city in the world.\n- A calculator app that uses JavaScript and React to create a functional and elegant calculator.",
        },
    ];

    // Define state variables for the selected feedback and the show feedback flag
    const [selectedFeedback, setSelectedFeedback] = useState(null);
    const [showFeedback, setShowFeedback] = useState(false);

    // Define a function to handle get feedback button click
    const handleGetFeedback = () => {
        setShowFeedback(true);
        // Fetch some dummy feedback data from an API or a mock file
        // For simplicity, we will use a mock file called feedback.json
        fetch("./feedback.json")
            .then((response) => response.json())
            .then((data) => {
                // Update state variable with feedback data
                setFeedback(data);
            })
            .catch((error) => {
                console.error(error);
            });
    };

    // Define a function to handle feedback selection
    const handleFeedbackSelect = (feedback) => {
        setSelectedFeedback(feedback); // Update state variable with selected feedback
    };

    // Define some dummy feedback data as an array of objects
    // This will be replaced by the actual data fetched from the API or mock file
    const feedback = [
        {
            id: 1,
            expert: "John Smith",
            rating: 4,
            comment:
                "Your cover letter is well-written and shows your interest and skills in web development. However, you could improve it by adding some specific examples of your achievements or challenges that you faced in your previous projects.",
            suggestions: [
                "For example, you could mention how you improved the performance or usability of a website that you worked on.",
                "You could also mention how you solved a problem or implemented a feature that was requested by a client or a user.",
                "These examples will demonstrate your abilities and value as a web developer.",
            ],
        },
        {
            id: 2,
            expert: "Jane Doe",
            rating: 5,
            comment:
                "Your resume is excellent and highlights your skills and experience as a web developer. It is clear, concise, and well-formatted. I don't have any major suggestions for improvement.",
            suggestions: [
                "The only minor thing I would suggest is to use bullet points instead of dashes for your skills list. This will make it easier to scan and read your skills.",
                "You could also add a link to your GitHub profile or any other online platforms where you showcase your work.",
            ],
        },
        {
            id: 3,
            expert: "Bob Lee",
            rating: 3,
            comment:
                "Your portfolio website is decent and shows some of your web development projects. However, it could be improved by making it more attractive and professional.",
            suggestions: [
                "You could use a different color scheme or font for your website. The current one is too plain and boring.",
                "You could add some animations or transitions to your website to make it more dynamic and engaging.",
                "You could also add some testimonials or reviews from your clients or users to show your credibility and reputation as a web developer.",
            ],
        },
    ];

    return (
        <div className="App">
            <h1>Apply to Recommended Jobs</h1>
            <p>Apply to up to 10 recommended jobs per day with just one click.</p>
            <button onClick={handleApplyNow}>Apply Now</button>
            {showNextSteps && (
                <div className="next-steps">
                    <h2>Next Steps</h2>
                    <div className="steps-container">
                        <div className="step">
                            <h3>Generate Personalized Application Materials</h3>
                            <p>
                                Generate customized cover letters, resumes, and
                                portfolios for each job you apply to.
                            </p>
                            <button onClick={handleGenerateMaterials}>
                                Generate Materials
                            </button>
                        </div>
                        <div className="step">
                            <h3>Find Your Perfect Matches</h3>
                            <p>
                                Find out which companies and roles are the best
                                fit for you based on your skills and preferences.
                            </p>
                            <MatchList
                                jobs={jobs}
                                selectedJob={selectedJob}
                                handleJobSelect={handleJobSelect}
                            />
                        </div>
                        {showMaterials && (
                            <div className="step">
                                <h3>Get Expert Feedback and Advice</h3>
                                <p>
                                    Receive personalized feedback and advice from
                                    industry experts on your application
                                    materials and interview skills.
                                </p>
                                <button onClick={handleGetFeedback}>
                                    Get Feedback
                                </button>
                            </div>
                        )}
                    </div>
                    {selectedJob && (
                        <JobDetails job={selectedJob} match={match} />
                    )}
                    {selectedMaterial && (
                        <MaterialDetails material={selectedMaterial} />
                    )}
                    {showFeedback && (
                        // Conditionally render the FeedbackList and FeedbackDetails components based on the showFeedback state variable
                        <div className="feedback-container">
                            <FeedbackList
                                feedback={feedback}
                                selectedFeedback={selectedFeedback}
                                handleFeedbackSelect={handleFeedbackSelect}
                            />
                            {selectedFeedback && (
                                <FeedbackDetails feedback={selectedFeedback} />
                            )}
                        </div>
                    )}
                </div>
            )}
        </div>
    );
}

export default App;
