// src/App.js

import React, { useState } from "react";
import "./App.css";
import JobList from "./JobList.js";
import JobDetails from "./JobDetails.js";
import MaterialList from "./MaterialList.js";
import MaterialDetails from "./MaterialDetails.js";

function App() {
    const [showNextSteps, setShowNextSteps] = useState(false);

    const handleApplyNow = () => {
        setShowNextSteps(true);
    };

    // Define state variables for the selected job and the match percentage
    const [selectedJob, setSelectedJob] = useState(null);
    const [match, setMatch] = useState(0);

    // Define a function to handle job selection
    const handleJobSelect = (job) => {
        setSelectedJob(job); // Update state variable with selected job
        setMatch(Math.floor(Math.random() * 100) + 1); // Update state variable with random match percentage
    };

    // Define some dummy jobs as an array of objects
    const jobs = [
        {
            id: 1,
            title: "Web Developer",
            company: "ABC Inc.",
            location: "New York, NY",
            salary: 80000,
        },
        {
            id: 2,
            title: "Graphic Designer",
            company: "XYZ Ltd.",
            location: "Los Angeles, CA",
            salary: 60000,
        },
        {
            id: 3,
            title: "Data Analyst",
            company: "123 Corp.",
            location: "Chicago, IL",
            salary: 70000,
        },
    ];

    // Define state variables for the selected material and the show materials flag
    const [selectedMaterial, setSelectedMaterial] = useState(null);
    const [showMaterials, setShowMaterials] = useState(false);

    // Define a function to handle generate materials button click
    const handleGenerateMaterials = () => {
        setShowMaterials(true);
    };

    // Define a function to handle material selection
    const handleMaterialSelect = (material) => {
        setSelectedMaterial(material); // Update state variable with selected material
    };

    // Define some dummy materials as an array of objects
    const materials = [
        {
            id: 1,
            type: "Cover Letter",
            title: "Web Developer at ABC Inc.",
            summary:
                "I'm a passionate web developer with over three years of experience in building responsive and user-friendly websites.",
            content:
                "Dear Hiring Manager,\n\nI'm writing to express my interest in the Web Developer position at ABC Inc. I have over three years of experience in building responsive and user-friendly websites using HTML, CSS, JavaScript, React, and other web technologies. I have also worked on several projects as part of a team, using agile methodologies and version control tools.\n\nOne of my recent projects was a website for XYZ Ltd., a graphic design company. I was responsible for creating the front-end of the website, using React and Bootstrap. I implemented features such as a dynamic portfolio gallery, a contact form, and a testimonial slider. I also ensured that the website was optimized for performance, accessibility, and SEO. The website received positive feedback from the client and the users.\n\nI'm impressed by the work that ABC Inc. does in creating innovative and engaging web solutions for various clients. I would love to join your team and contribute to your projects with my skills and enthusiasm. I'm confident that I can be an asset to your company and help you achieve your goals.\n\nI have attached my resume and portfolio for your review. Please let me know if you have any questions or would like to schedule an interview. You can reach me at (123)456-7890 or email@example.com.\n\nThank you for your consideration.\n\nSincerely,\nK",
        },
        {
            id: 2,
            type: "Resume",
            title: "Web Developer",
            summary:
                "A web developer with over three years of experience in building responsive and user-friendly websites using HTML, CSS, JavaScript, React, and other web technologies.",
            content:
                "K\nWeb Developer\n(123)456-7890 | email@example.com | www.kportfolio.com\n\nSummary\nA web developer with over three years of experience in building responsive and user-friendly websites using HTML, CSS, JavaScript, React, and other web technologies. Skilled in working on both front-end and back-end development, as well as collaborating with other developers in a team environment.\n\nSkills\n- HTML, CSS, JavaScript, jQuery, Bootstrap\n- React, Redux, Next.js, Gatsby\n- Node.js, Express, MongoDB, Firebase\n- Git, GitHub, npm, webpack\n- Responsive design, accessibility, SEO\n\nExperience\nWeb Developer | XYZ Ltd. | Jan 2020 - Present\n- Developed the front-end of a website for a graphic design company using React and Bootstrap\n- Implemented features such as a dynamic portfolio gallery, a contact form, and a testimonial slider\n- Optimized the website for performance, accessibility, and SEO\n- Collaborated with the back-end developer and the designer using agile methodologies and Git\n\nWeb Developer | Freelance | Jun 2018 - Dec 2019\n- Built various websites for clients using HTML, CSS, JavaScript, jQuery, and other web technologies\n- Created custom themes and plugins for WordPress sites\n- Integrated APIs and databases using Node.js, Express, MongoDB, and Firebase\n- Tested and debugged websites using Chrome DevTools and Postman\n\nEducation\nBachelor of Science in Computer Science | ABC University | Sep 2016 - May 2020\n- Graduated with honors and a GPA of 3.8\n- Completed courses in web development, data structures, algorithms, databases, and software engineering\n- Participated in several hackathons and won the first prize in one of them\n\nPortfolio\nwww.kportfolio.com | A personal portfolio website showcasing my projects and skills\nwww.xyz.com | A website for XYZ Ltd., a graphic design company\nwww.abc.com | A website for ABC Inc., a web development company",
        },
        {
            id: 3,
            type: "Portfolio",
            title: "Web Development Projects",
            summary:
                "A collection of web development projects that I have worked on or created using HTML, CSS, JavaScript, React, and other web technologies.",
            content:
                "Web Development Projects\n\nHere are some of the web development projects that I have worked on or created using HTML, CSS, JavaScript, React, and other web technologies. You can view the live demos and the source code by clicking on the links below.\n\nProject 1: Personal Portfolio Website\n- A personal portfolio website showcasing my projects and skills\n- Built with HTML, CSS, JavaScript, and Bootstrap\n- Live demo: www.kportfolio.com\n- Source code: https://github.com/kportfolio/kportfolio.github.io\n\nProject 2: Website for XYZ Ltd.\n- A website for XYZ Ltd., a graphic design company\n- Built with React and Bootstrap\n- Implemented features such as a dynamic portfolio gallery, a contact form, and a testimonial slider\n- Optimized for performance, accessibility, and SEO\n- Live demo: www.xyz.com\n- Source code: https://github.com/kportfolio/xyz-website\n\nProject 3: Website for ABC Inc.\n- A website for ABC Inc., a web development company\n- Built with Next.js and Gatsby\n- Used static site generation and server-side rendering for fast loading and SEO benefits\n- Integrated a blog feature using Markdown files and GraphQL queries\n- Live demo: www.abc.com\n- Source code: https://github.com/kportfolio/abc-website",
        },
    ];

    return (
        <div className="App">
            <header>
                <h1>Welcome to the Job Application Disruption System</h1>
            </header>
            <main>
                <section>
                    <h2>Apply to Recommended Jobs</h2>
                    <p>Apply to up to 10 recommended jobs per day with just one click.</p>
                    <button onClick={handleApplyNow}>Apply Now</button>
                </section>
                {showNextSteps && <JobList jobs={jobs} handleJobSelect={handleJobSelect} />}
                {selectedJob && <JobDetails job={selectedJob} match={match} />}
                <section>
                    <h2>Generate Personalized Application Materials</h2>
                    <p>Let our AI create tailored cover letters, resumes, and portfolios for each job.</p>
                    <button onClick={handleGenerateMaterials}>Generate Materials</button>
                </section>
                {showMaterials && (
                    <MaterialList materials={materials} handleMaterialSelect={handleMaterialSelect} />
                )}
                {selectedMaterial && <MaterialDetails material={selectedMaterial} />}
                <section>
                    <h2>Find Your Perfect Match</h2>
                    <p>Discover the best companies and roles based on your skills and preferences.</p>
                    <button>Find Matches</button>
                </section>
                <section>
                    <h2>Get Instant Feedback and Guidance</h2>
                    <p>Receive real-time feedback on your applications, interviews, and offers.</p>
                    <button>Get Feedback</button>
                </section>
            </main>
            <footer>
                <p>Job Application Disruption System © 2023</p>
            </footer>
        </div>
    );
}

export default App;
