import React, { useState, useEffect, useRef } from "react";

function Chatbot() {
    const [messages, setMessages] = useState([]);
    const [inputValue, setInputValue] = useState("");
    const [chatbotVisible, setChatbotVisible] = useState(true); // State to track chatbot visibility

    const botName = "JobBot"; // Name of the chatbot

    const chatbotRef = useRef(null);

    const addMessage = (text, isUser = false) => {
        setMessages((prevMessages) => [
            ...prevMessages,
            { text, isUser, timestamp: new Date().toISOString() },
        ]);
    };

    useEffect(() => {
        // Welcome message from the chatbot
        if (messages.length === 0) {
            addMessage(`Hello! I'm ${botName}, your job application assistant. How can I help you?`);
        }
    }, [messages]);

    const handleUserMessage = () => {
        if (inputValue.trim() === "") return;

        // Add the user message to the chat
        addMessage(inputValue, true);

        // Basic NLP to provide responses based on user input
        const lowerCaseInput = inputValue.toLowerCase().trim();
        let botResponse = "";

        switch (lowerCaseInput) {
            case "hello":
                botResponse = "Hello! How can I assist you today?";
                break;
            case "how can I apply to a job?":
                botResponse = "You can apply to recommended jobs by clicking the 'Apply Now' button.";
                break;
            case "thank you":
                botResponse = "You're welcome! If you have any more questions, feel free to ask.";
                break;
            default:
                botResponse = "I'm sorry, I'm still learning. Can you please rephrase your question?";
        }

        // Add the bot's response to the chat
        addMessage(botResponse, false);
        setInputValue("");
    };

    // ... Other functions and useEffects for handling messages ...

    const handleMinimize = () => {
        setChatbotVisible((prevState) => !prevState); // Toggle chatbot visibility
    };

    return (
        <div className={`chatbot ${chatbotVisible ? "visible" : "hidden"}`}>
            <div className="chatbot-header">
                {botName}
                {chatbotVisible ? (
                    <button className="minimize-button" onClick={handleMinimize}>
                        Minimize
                    </button>
                ) : (
                    <button className="maximize-button" onClick={handleMinimize}>
                        Maximize
                    </button>
                )}
            </div>
            {chatbotVisible && (
                <>
                    <div className="chatbot-messages" ref={chatbotRef}>
                        {messages.map((message, index) => (
                            <div key={index} className={`message ${message.isUser ? "user" : "bot"}`}>
                                <span className="message-text">{message.text}</span>
                            </div>
                        ))}
                    </div>
                    <div className="chatbot-input">
                        <input
                            type="text"
                            value={inputValue}
                            onChange={(e) => setInputValue(e.target.value)}
                            placeholder="Type your message..."
                        />
                        <button onClick={handleUserMessage}>Send</button>
                    </div>
                </>
            )}
        </div>
    );
}

export default Chatbot;
