// src/App.js

import React, { useState } from "react";
import "./App.css";
import JobList from "./JobList.js";
import JobDetails from "./JobDetails.js";
import MaterialList from "./MaterialList.js";
import MaterialDetails from "./MaterialDetails.js";
import MatchList from "./MatchList.js";
import FeedbackList from "./FeedbackList.js";

function App() {
    const [showNextSteps, setShowNextSteps] = useState(false);

    const handleApplyNow = () => {
        setShowNextSteps(true);
    };

    // Define state variables for the selected job and the match percentage
    const [selectedJob, setSelectedJob] = useState(null);
    const [match, setMatch] = useState(0);

    // Define a function to handle job selection
    const handleJobSelect = (job) => {
        setSelectedJob(job); // Update state variable with selected job
        setMatch(Math.floor(Math.random() * 100) + 1); // Update state variable with random match percentage
    };

    // Define some dummy jobs as an array of objects
    const jobs = [
        {
            id: 1,
            title: "Web Developer",
            company: "ABC Inc.",
            location: "New York, NY",
            salary: 80000,
        },
        {
            id: 2,
            title: "Graphic Designer",
            company: "XYZ Ltd.",
            location: "Los Angeles, CA",
            salary: 60000,
        },
        {
            id: 3,
            title: "Data Analyst",
            company: "123 Corp.",
            location: "Chicago, IL",
            salary: 70000,
        },
    ];

    return (
        <div className="App">
            <header>
                <h1>Welcome to the Job Application Disruption System</h1>
            </header>
            <main>
                <section>
                    <h2>Apply to Recommended Jobs</h2>
                    <p>Apply to up to 10 recommended jobs per day with just one click.</p>
                    <button onClick={handleApplyNow}>Apply Now</button>
                </section>
                {showNextSteps && (
                    <section>
                        <h2>Next Steps</h2>
                        <p>Follow these steps to complete your job applications:</p>
                        <ol>
                            <li>Review the recommended jobs and their descriptions.</li>
                            <li>Customize your application materials (cover letter, resume, and portfolio) for each job.</li>
                            <li>Submit your applications through the provided application links.</li>
                            <li>Keep track of your applications and monitor their progress.</li>
                        </ol>
                    </section>
                )}
                <section>
                    <h2>Generate Personalized Application Materials</h2>
                    <p>Let our AI create tailored cover letters, resumes, and portfolios for each job.</p>
                    <MaterialList selectedJob={selectedJob} />
                    <MaterialDetails selectedJob={selectedJob} />
                </section>
                <section>
                    <h2>Find Your Perfect Matches</h2>
                    <p>Discover the best companies and roles based on your skills and preferences.</p>
                    <JobList jobs={jobs} handleJobSelect={handleJobSelect} />
                    <JobDetails selectedJob={selectedJob} match={match} />
                </section>
                <section>
                    <h2>Get Expert Feedback and Advice</h2>
                    <p>Receive personalized feedback from industry experts on your application materials and interview skills.</p>
                    <FeedbackList selectedJob={selectedJob} />
                </section>
            </main>
            <footer>
                <p>Job Application Disruption System &copy; 2023</p>
            </footer>
        </div>
    );
}

export default App;
